import React, { Component } from 'react';
import { ProductConsumer } from '../context';
import { Link } from 'react-router-dom';
import { Modal, Button, Container, Image} from 'react-bootstrap';


function MyModal() {

  return ( 
      <ProductConsumer>
          {value => {
              const { modalOpen, openModal, closeModal } = value;
              const { title, img, price, inCart } = value.modalProduct;
                  return (
                    <>
                      <Modal
                        show={modalOpen}
                        // onHide={handleClose}
                        backdrop="static"
                        keyboard={false}
                      >
                        <Modal.Header>
                          <h1>Добавлено в корзину</h1>
                        </Modal.Header>
                        <Modal.Body>
                          <Container className="text-center">
                            <Image src={img} fluid />
                            <div className="d-flex justify-content-between">
                              <h5>{title}</h5>
                              <p>{price} руб</p>
                            </div>
                          </Container>
                        </Modal.Body>
                        <Modal.Footer>
                          <Link to="/products">
                            <Button variant="secondary" onClick={() => {closeModal()}}>
                              К товарам
                            </Button>
                          </Link>
                          <Link to="/cart">
                            <Button variant="primary" onClick={() => {closeModal()}}>
                              В корзину
                            </Button>
                          </Link>
                        </Modal.Footer>
                      </Modal>
                    </>
                  );
          }}
      </ProductConsumer>
    );
}

export default MyModal;
