import React, { Component } from 'react';
import { Col, Card, Button, Container } from 'react-bootstrap';


const Product = (props) => {
    const { id, title, img, price, inCart } = props.product;
    return (
            <Col className="mb-3" xs="9" sm="6" lg="4" xl="3">
                    <Card 
                        style={{ width: '18rem' }} 
                        className="m-auto" 
                        onClick={() => {console.log('Product clicked')}}
                    >
                        <a href="/details">
                            <Card.Img 
                                variant="top" 
                                src={img} 
                                alt="product" 
                                className="p-5"  
                            />
                        </a>
                        <Card.Body className="p-0">
                            <Button 
                                variant="primary" 
                                disabled={inCart} 
                            >
                                {inCart ? "В корзине" : "Добавить"}
                            </Button>
                            <Card.Footer className="d-flex justify-content-center">
                                <p className="align-self-center-between">{title}</p>
                                <h5>${price}</h5>
                            </Card.Footer>
                        </Card.Body>
                    </Card>
            </Col>
    );
}




export default Product;
