import React, { Component } from 'react';
import { detailProduct, storeProducts } from './data';

const ProductContext = React.createContext();

class ProductProvider extends Component {
    state = {
        products: [],
        detailProduct: detailProduct
    }

    componentDidMount() {
        this.setProducts();
    }

    //method to avoid passing of refferences to storeProducts items but instead passing the values to the state
    setProducts() { 
        let tempProducts = [];
        storeProducts.forEach(item => {
            const singleItem = {...item};
            tempProducts.push(singleItem);
        });
        this.setState(() => {
            return {products: tempProducts}
        });
    }

    handleDetails = (id) => {
        let tempProduct;
        this.state.products.forEach( item => {
            if (item.id === id) {
                tempProduct = {...item};
                console.log('I found it! it is:');
                console.log({...item});
            }
        });
        this.setState(() => {
            console.log(tempProduct)
            return {detailProduct: tempProduct}
        });
        console.log(this.state.detailProduct)
    }

    addToCart() {
        console.log('hello from addToCart');
    }

    render() { 
        return ( 
            <ProductContext.Provider value={{
                ...this.state, //pass the whole state to this object
                handleDetails: this.handleDetails,
                addToCart: this.addToCart,
            }}>
                {this.props.children}
            </ProductContext.Provider>
         );
    }
}

const ProductConsumer = ProductContext.Consumer;

export {ProductProvider, ProductConsumer};
