import React, { Component } from 'react';
import CartColumns from './CartColumns';
import CartEmpty from './CartEmpty';
import {ProductConsumer} from '../../context';

class Cart extends Component {
    state = {  }
    render() { 
        return ( 
            <ProductConsumer>
                { values => {
                    const {Cart} = values; 
                        if (Cart.length > 0) {
                            return (
                                <>
                                    <CartColumns />
                                </>
                            );
                        } else {
                            return ( <CartEmpty /> );
                        }

                }}
            </ProductConsumer>

         );
    }
}

export default Cart;
