import React, { Component } from 'react';
import { Col, Card, Button, Container } from 'react-bootstrap';
import {PropTypes} from 'prop-types';


const Product = (props) => {
    const { id, title, img, price, inCart } = props.product;
    return (
            <Col className="my-3 mx-auto" xs="9" sm="6" lg="4" xl="3">
                    <Card onClick={() => {props.handleDetails(id)}}>
                        <a href="/details">
                            <Card.Img 
                                variant="top" 
                                src={img} 
                                alt="product" 
                                className="p-5"
                            />
                        </a>
                        <Card.Body className="p-0">
                            <div className="d-flex justify-content-end">
                                <Button 
                                    variant="success" 
                                    disabled={inCart} 
                                >
                                    {inCart ? "В корзине" : "Добавить"}
                                </Button>
                            </div>
                            <Card.Footer className="d-flex justify-content-between">
                                <p className="align-self-center mb-0">{title}</p>
                                <h5 className="mb-0">${price}</h5>
                            </Card.Footer>
                        </Card.Body>
                    </Card>
            </Col>
    );
}
//Setting the required types for the props values (throws an error to the console, if not matched)
Product.propTypes = {
    product: PropTypes.shape({
        id: PropTypes.number,
        img: PropTypes.string,
        title: PropTypes.string,
        price: PropTypes.number,
        inCart: PropTypes.bool,
        total: PropTypes.number
    }).isRequired
}


export default Product;
