import React, { Component } from 'react';
import { Col, Container, Row, Image, Button} from 'react-bootstrap';
import { ProductConsumer } from '../context';
import { Link } from 'react-router-dom';
import { storeProducts } from '../data';

class Details extends Component {

    state = {
        productDetails: {}
    }

    componentDidMount() {
        console.log(this.props)
        console.log("params id is: " + this.props.match.params.id)
        this.handleDetails();
    }


    handleDetails = () => {
        let product;
        storeProducts.forEach( item => {
            if (item.id === id) {
                product = {...item};
            }
        });
        this.setState(() => {
            return {productDetails: product}
        });
    }


    render() { 
        return(
            <div>
            <ProductConsumer>
                    {value => {
                        const { title, img, price, company, info, inCart } = this.state.productDetails;
                        return (
                            <Container className="my-5">
                                <Row>
                                    <Col className="my-3 mx-auto" xs="9" md="6">
                                        <Image src={img} fluid />
                                    </Col>
                                    <Col className="my-3 mx-auto" xs="9" md="6">
                                        <h2>{title}</h2>
                                        <p><strong>Производитель:</strong> {company}</p>
                                        <p><strong>Цена:</strong> ${price}</p>
                                        <p><strong>Информация о продукте:</strong> <br/> {info}</p>
                                        <Link to="/products">
                                            <Button className="mx-2" variant="primary">Назад к списку</Button>
                                        </Link>
                                        {/* <a className="btn btn-primary mx-2" href="/" role="button">Назад к списку</a> */}
                                        <Button className="mx-2" variant="success">{inCart ? "В корзине" : "Добавить"}</Button>
                                    </Col>
                                </Row>
                            </Container>
                        )
                    }}
            </ProductConsumer>
            {() => {console.log("hello from below the product consumer")}}
            </div>
        );
    }
}

export default Details;
