import React, { Component } from 'react';
import { Col, Container, Row, Image} from 'react-bootstrap';
import { ProductConsumer } from '../context';

const Details = () => {
    return(
        <div>
            <ProductConsumer>
                {value => {
                    const { id, title, img, price, company } = value.detailProduct;
                    return (
                        <Container>
                            <Row>
                                <Col className="my-3">
                                    <Image src={img} fluid />
                                </Col>
                                <Col className="my-3">
                                    <h2>{title}</h2>
                                </Col>
                            </Row>
                        </Container>
                    )
                }}
            </ProductConsumer>
        </div>
    )
}

export default Details;
