import React, { Component } from 'react';
import { Col, Container, Row, Image, Button} from 'react-bootstrap';
import { ProductConsumer } from '../context';

const Details = () => {
    return(
        <div>
            <ProductConsumer>
                {value => {
                    const { id, title, img, price, company, info, inCart } = value.detailProduct;
                    return (
                        <Container className="my-3">
                            <Row>
                                <Col className="my-3">
                                    <Image src={img} fluid />
                                </Col>
                                <Col className="my-3">
                                    <h2>{title}</h2>
                                    <p>Производитель: {company}</p>
                                    <p>Цена: ${price}</p>
                                    <p>Информация о продукте: <br/> {info}</p>
                                    <a className="btn btn-primary mx-2" href="/" role="button">Назад к списку</a>
                                    <Button className="mx-2" variant="success">{inCart ? "В корзине" : "Добавить"}</Button>
                                </Col>
                            </Row>
                        </Container>
                    )
                }}
            </ProductConsumer>
        </div>
    )
}

export default Details;
