import React, { Component } from 'react';
import { Col, Container, Row, Image, Button} from 'react-bootstrap';
import { ProductConsumer } from '../context';
import { Link } from 'react-router-dom';
import { detailProduct, storeProducts } from './data';

class Details extends Component {

    state = {
        products: [],
        productDetails: {}
    }

    componentDidMount() {
        console.log("Details has mounted")
    }

    render() { 
        return(
            <ProductConsumer>
                    {value => {
                        // if (value.productDetails === undefined) {
                        //     console.log("hello from productDetails undefinded");
                        //     value.setProducts();
                        //     value.handleDetails();
                        // }
                        const { title, img, price, company, info, inCart } = value.productDetails;
                        return (
                            <Container className="my-5">
                                <Row>
                                    <Col className="my-3 mx-auto" xs="9" md="6">
                                        <Image src={img} fluid />
                                    </Col>
                                    <Col className="my-3 mx-auto" xs="9" md="6">
                                        <h2>{title}</h2>
                                        <p><strong>Производитель:</strong> {company}</p>
                                        <p><strong>Цена:</strong> ${price}</p>
                                        <p><strong>Информация о продукте:</strong> <br/> {info}</p>
                                        <Link to="/products">
                                            <Button className="mx-2" variant="primary">Назад к списку</Button>
                                        </Link>
                                        {/* <a className="btn btn-primary mx-2" href="/" role="button">Назад к списку</a> */}
                                        <Button className="mx-2" variant="success">{inCart ? "В корзине" : "Добавить"}</Button>
                                    </Col>
                                </Row>
                            </Container>
                        )
                    }}
            </ProductConsumer>
        );
    }
}

export default Details;
