import React, { Component } from 'react';
import { Col, Card, Button, Container } from 'react-bootstrap';


const Product = (props) => {
    const { id, title, img, price, inCart } = props.product;
    return (
            <Col className="mb-3" xs="9" sm="6" lg="4" xl="3">
                    <Card style={{ width: '18rem' }} className="m-auto">
                        <a src="/details">
                            <Card.Img variant="top" src={img} alt="product" className="p-5" />
                        </a>
                        <Card.Body>
                            <Card.Title>{title}</Card.Title>
                            <Card.Text>
                                Some quick example text to build on the card title and make up the bulk of
                                the card's content. {price}$
                            </Card.Text>
                        <Button variant="primary">{inCart ? "В корзине" : "Добавить"}</Button>
                        </Card.Body>
                    </Card>
            </Col>
    );
}




export default Product;
